import axios from 'axios';
import {  BrowserRouter as Router, Route, Link ,Switch} from "react-router-dom";
import Search from './Search'
import Cart from './Cart'
import './styles/shop.scss'
import Login from './Login'
import menGif from './Assets/men.gif'
// import Dropdown from 'react-bootstrap/Dropdown'

export default class Navigation extends Component {
    constructor(props) {
        super(props);
        this.state={
         menGif:'',   
        searchKey:'',
        srch:'',
        count:'',
        signIn:false
        }
    }


    componentDidMount() {
        // this.getApi();

        axios({
            "method":"GET",
            "url":"https://apidojo-forever21-v1.p.rapidapi.com/products/search",
            "headers":{
            "content-type":"application/octet-stream",
            "x-rapidapi-host":"apidojo-forever21-v1.p.rapidapi.com",
            "x-rapidapi-key":"0c50512463mshf6956ddd7cdbe33p13858djsn036596cdcff1",
            "useQueryString":true
            },"params":{
            "color_groups":"black",
            "start":"0",
            "rows":"20",
            "query":"men"
            }
            })
            .then((response)=>{
            //   console.log(response)
            })
            .catch((error)=>{
              console.log(error)
            })
    }


    getApi=async()=> {
        var htmlArr=[]
        await fetch(`${'https://cors-anywhere.herokuapp.com/'}'https://glacial-woodland-21756.herokuapp.com/giphy/v1/visitor/count`, {
            method:'GET',
           headers: {
             'Accept': 'application/json',
             'Content-Type': 'application/json'
           }
           }) .then(res => res.json()) .then(data=>{
            this.setState({count:data})
           }).catch((err) => 
           console.log (err)
           )
   }
    handleChange=(e)=>{
        e.preventDefault()
        this.setState({srch:e.target.value})
    }

    senttoChild=()=>{


        this.setState({signIn:true})

    }

    showGif=(e)=>{
        e.preventDefault()
        setInterval(()=>{ this.timerFn() }, 30);

        }
