      this.setState({item1:inputArr[0]})
      this.setState({item2:inputArr[1]})
      }
      else if(inputArr.length==3){
        this.setState({item1:inputArr[0]})
        this.setState({item2:inputArr[1]})
        this.setState({item3:inputArr[2]})
        }
        else if(inputArr.length==4){
          this.setState({item1:inputArr[0]})
          this.setState({item2:inputArr[1]})
          this.setState({item3:inputArr[2]})
          this.setState({item4:inputArr[3]})
          }
          else if(inputArr.length==5){
            this.setState({item1:inputArr[0]})
            this.setState({item2:inputArr[1]})
            this.setState({item3:inputArr[2]})
